package UttaraSamples;

public class MariDog extends Dog {

	public MariDog(String name) {
		super(name);
		// TODO Auto-generated constructor stub
	}

	public void sleep() {
		System.out.println("MariDog of name " + name + " is sleeping...");
	}
	
	public void play() {
		System.out.println("MariDog of name " + name + " is playing...");
	}

	
}
